import styled from '@emotion/styled';
export const Box = styled.div`
  display: flex;
`;
export const Button = styled.button`
  &:not(:last-child) {
    margin-right: 20px;
  }
  font-size: 18px;
  text-transform: uppercase;
  padding: 15px 30px;
  background-color: #abdbe3;
  color: black;
  border-radius: 10px;
  border: none;
  cursor: pointer;
  &:hover,
  &:focus {
    background-color: #1e81b0;
    box-shadow: 8px 8px 24px 4px rgba(0,0,0,0.75);
  }
`;
