import styled from '@emotion/styled';
export const Box = styled.div`

  padding: 20px;
  width: 400px;
  text-transform: uppercase;
  font-size: 25px;
  font-weight: 600;
`;
export const Item = styled.p`
  display: flex;
  justify-content: space-between;
  align-items: center;
