import styled from '@emotion/styled';
export const Box = styled.div`

  padding: 30px;
  width: 400px;
  text-transform: uppercase;
  font-size: 30px;
  font-weight: bold;
`;
export const Item = styled.p`
  display: flex;
  justify-content: space-between;
  align-items: center;

  padding: 10px;
  color: #bad7e5;
`;
